import {Component, OnInit, Input, Output, EventEmitter} from '@angular/core';
import { Router } from '@angular/router';
import {Blog} from '../models/blog';
import {BlogService} from "../service/blog.service";

@Component({
  selector: 'app-preview-blog',
  templateUrl: './preview-blog.component.html',
  styleUrls: []
})
export class PreviewBlogComponent implements OnInit {
  @Input() blog: Blog;

  @Output() deleteEvent = new EventEmitter();

  constructor(private router: Router, private blogService: BlogService) {}

  ngOnInit() {}

  public navigate(): void {
    this.router.navigateByUrl('/blogs/' + this.blog.id);
  }

  public deleteBlog() {
    this.blogService.deleteBlog(this.blog.id).subscribe(res => {
      console.log('Deleted Blog' + this.blog.id);
      this.deleteEvent.emit(this.blog);
    });
  }

}
<div class="post-preview">
  <a (click)="navigate()" class="pointer">
    <h2 class="post-title" [textContent]="blog.title">
    </h2>
    <h3 class="post-subtitle">{{ blog.description.length > 48 ? blog.description.substring(0, 50)+"..." : blog.description }}
    </h3>
  </a>
      <!-- Delete Button -->
  <div class="clearfix text-center float-right mx-auto">
    <a (click)="deleteBlog();" class="btn btn-light pointer">
      Delete
      <i class="" aria-hidden="true"></i></a>
  </div>
  <p class=" post-meta">Posted at :
    <a href="#" [textContent]="blog.created_date"></a>
  </p>


</div>
