import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Blog } from '../models/blog';
import {BlogService} from '../service/blog.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: []
})
export class HomeComponent implements OnInit {
  loading: boolean = true;
  addDummyText: string = 'add Dummy';
  blogs: Blog[];

  constructor(private blogService: BlogService) {}

  ngOnInit() {
    this.getBlogs();
  }

  private getBlogs(): void {
    this.blogService.getBlogs().subscribe(blogs => {
      this.blogs = blogs;
      this.loading = false;
    });
  }

  public addDummy(): void {
    if(this.addDummyText == 'add Dummy') {
      console.log("sending dummy");
      this.blogService.addDummy(2).subscribe(res => {
      console.log('Dummy add request sent');
      });
      this.addDummyText = 'Request Sent';
      setInterval(() => this.getBlogs(),10000); //Poll the blogs api every 10 seconds
    }
  }
  public refresh(event): void {
    this.blogs = this.blogs.filter(item => item != event);
  }
}
<h1 style="text-align: center; margin-bottom: 12px; color: lightslategray;">All Blogs</h1>
<app-loading *ngIf="loading"></app-loading>
<div class="row" *ngIf="!loading">
  <div class="col-lg-8 col-md-10 mx-auto">
    <div *ngFor="let blog of blogs">
      <app-preview-blog [blog]="blog" (deleteEvent)="refresh($event)"></app-preview-blog>
      <hr>
    </div>

    <div class="clearfix">
      <a class="btn btn-secondary float-left pointer" routerLink="/add-blog">
        Add Blog
        <i class="fa fa-plus" aria-hidden="true"></i></a>
      <a class="btn btn-secondary float-right pointer" (click)="addDummy();">
        {{addDummyText}}
        <i class="fa fa-plus" aria-hidden="true"></i></a>
    </div>
  </div>
</div>
