import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-masthead',
  templateUrl: './masthead.component.html',
  styleUrls: []
})
export class MastheadComponent implements OnInit {

  constructor() { }

  ngOnInit() {
  }

}
<header class="masthead" style="background-image: url('../../assets/images/home-bg.jpg')">
  <div class="overlay"></div>
  <div class="container">
    <div class="row">
      <div class="col-lg-8 col-md-10 mx-auto">
        <div class="site-heading">
          <h1 ng-bind="header.title"></h1>
          <span class="subheading" ng-bind="header.subtitle"></span>
          <span class="meta" ng-if="header.byline.length > 0" ng-bind="header.byline">
          </span>
        </div>
      </div>
    </div>
  </div>
</header>
