import { Component, OnInit } from "@angular/core";
import { Router } from "@angular/router";
import { ActivatedRoute } from "@angular/router";
import {BlogService} from "../service/blog.service";
import {Blog} from "../models/blog";

@Component({
  selector: "app-view-blog",
  templateUrl: "./view-blog.component.html",
  styleUrls: []
})
export class ViewBlogComponent implements OnInit {
  loading: boolean = true;
  blog: Blog;
  editable: boolean = false;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private blogService: BlogService
  ) {}

  ngOnInit() {
    this.getBlog();
    console.log(this.blog);
  }

  public deleteBlog() {
    const id = this.route.snapshot.paramMap.get('id');
    this.blogService.deleteBlog(id).subscribe(res => {
      console.log('Deleted Blog' + id);
      this.router.navigate(['/home']);
    });
  }

  public editableToggle() {
    this.editable = !this.editable;
  }

  private getBlog(): void {
    const id = this.route.snapshot.paramMap.get('id');
    console.log('id: ' + id);

    this.blogService.getBlog(id).subscribe(blog => {
      console.log('blog: ' + JSON.stringify(blog));
      this.blog = blog;
      this.loading = false;
    });
  }
}
<div class="row" *ngIf="loading">
  <div class="col-12 text-center">
    <i class="fa fa-spinner fa-pulse fa-3x fa-fw"></i>
    <span class="sr-only">Loading...</span>
  </div>
</div>
<div class="row" *ngIf="!loading">
    <div *ngIf="!editable" class="col-lg-8 col-md-10 mx-auto">
      <h1 [textContent]="blog.title"></h1>

      <h3 class="post-content" [textContent]="blog.description"></h3>

      <p style="color: grey; text-align: center;" class="post-meta"><i>Posted On :  </i>
        <i><a href="#" style="color: grey; text-decoration: none;" [textContent]="blog.created_date"></a></i>
      </p>
    </div>

    <!-- Update Button -->
    <div *ngIf="!editable" class="clearfix text-center float-left mx-auto">
      <a (click)="editableToggle();" class="btn btn-light pointer">
        Edit
        <i class="" aria-hidden="true"></i></a>
    </div>

    <!-- Delete Button -->
    <div *ngIf="!editable" class="clearfix text-center float-right mx-auto">
      <a (click)="deleteBlog();" class="btn btn-light pointer">
        Delete
        <i class="" aria-hidden="true"></i></a>
    </div>
    <div *ngIf="editable" class="col-lg-8 col-md-10 mx-auto">
    <app-add-blog [blog]="blog" [edit]="true"></app-add-blog>
    </div>
</div>
